import React from 'react'
import { useState, useEffect } from 'react';
let id = 0
const A = () => {
    const [Data, setData] = useState("")
    const [List, setList] = useState([])
    const [Bol, setBol] = useState(false)
    const handlerAdd = () => {
        let _Data = Data
        const _list = List
        setList([..._list, {
            id: id++,
            Data: _Data
        }])
    }
    const handlerDel = (eve, index) => {
        const _list = [...List]
        _list.splice(index, 1)
        setList([..._list])
    }

    useEffect(() => {
        console.log(List);
    }, [List])

    return (
        <>
            <h1> 这是A组件</h1>
            <input type="text" id=''
                value={Data} onChange={(eve) => { setData(eve.target.value) }}
            />
            <button onClick={() => handlerAdd()}>确定</button>
            <hr />
            {List.length === 0 && <h3 >暂无留言</h3>}
            <ul>
                {List.map((item, index) => (
                    <li key={item.id}>
                        {item.Data}
                        <button
                            onClick={(eve) => handlerDel(eve, index)}
                            style={{ marginLeft: 50 }}>删除</button>
                        <button
                        onClick={()=>setBol(true) }
                            style={{ marginLeft: 50 }}>修改</button>
                    </li>
                )
                )}
            </ul>
            {Bol && <input type="text" ></input> }
        </>

    )
}

export default A
